import React, { useCallback } from 'react';
import { useTodos } from './hooks/useTodos';
import TodoForm from './components/TodoForm';
import TodoList from './components/TodoList';
import TodoStats from './components/TodoStats';
import TodoFilter from './components/TodoFilter';
import ErrorMessage from './components/ErrorMessage';
import { TodoFormData } from './types/Todo';

const App: React.FC = () => {
  const {
    todos,
    loading,
    error,
    filter,
    stats,
    setFilter,
    fetchTodos,
    createTodo,
    updateTodo,
    toggleTodo,
    deleteTodo,
  } = useTodos();

  const handleCreateTodo = useCallback(async (todoData: TodoFormData) => {
    await createTodo(todoData);
  }, [createTodo]);

  const handleUpdateTodo = useCallback(async (id: number, data: Partial<TodoFormData>) => {
    await updateTodo(id, data);
  }, [updateTodo]);

  const handleToggleTodo = useCallback(async (id: number) => {
    await toggleTodo(id);
  }, [toggleTodo]);

  const handleDeleteTodo = useCallback(async (id: number) => {
    await deleteTodo(id);
  }, [deleteTodo]);

  const handleFilterChange = useCallback((newFilter: 'all' | 'completed' | 'pending') => {
    setFilter(newFilter);
  }, [setFilter]);

  const handleRetry = useCallback(() => {
    fetchTodos();
  }, [fetchTodos]);

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 py-8 px-4">
      <div className="max-w-4xl mx-auto">
        {/* Header */}
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold text-gray-800 mb-2">
            Todo Application
          </h1>
          <p className="text-gray-600">
            Manage your tasks with ease using React, TypeScript, and JSONPlaceholder API
          </p>
        </div>

        {/* Error Message */}
        {error && (
          <ErrorMessage 
            message={error} 
            onRetry={handleRetry}
          />
        )}

        {/* Statistics */}
        <TodoStats stats={stats} />

        {/* Filter */}
        <TodoFilter 
          currentFilter={filter}
          onFilterChange={handleFilterChange}
          stats={stats}
        />

        {/* Add Todo Form */}
        <TodoForm 
          onSubmit={handleCreateTodo}
          loading={loading}
        />

        {/* Todo List */}
        <TodoList
          todos={todos}
          onToggle={handleToggleTodo}
          onUpdate={handleUpdateTodo}
          onDelete={handleDeleteTodo}
          loading={loading}
        />

        {/* Footer */}
        <div className="text-center mt-8 text-gray-500 text-sm">
          <p>
            Powered by{' '}
            <a 
              href="http://jsonplaceholder.typicode.com/todos" 
              target="_blank" 
              rel="noopener noreferrer"
              className="text-blue-600 hover:text-blue-800 underline"
            >
              JSONPlaceholder API
            </a>
          </p>
          <p className="mt-1">
            Built with React, TypeScript, and Tailwind CSS
          </p>
        </div>
      </div>
    </div>
  );
};

export default App;
